mapper.select:
select cp.* , mm.* , me.*, tm.*, tn.* from choose_projectname cp inner join member_meal mm on cp.mm_id=mm.mm_id inner join `member` me on me.member_id =mm.member_id inner join team_meal tm on tm.team_id=cp.choose_id inner join team_projectname tn on tn.tp_id = cp.ptp_id where cp.emp_id=#{query.empId} and me.member_state !=1
and mm.meal_type=#{query.mealType}
and me.member_name=#{query.memberName}

select distinct me.member_id ,me.member_age,me.member_name ,me.member_phone,me.member_sex from choose_projectname cp inner join member_meal mm on cp.mm_id=mm.mm_id inner join `member` me on me.member_id =mm.member_id where cp.emp_id=49 and mm.meal_type="团操" and me.member_state !=1
and me.member_name=#{query.memberName}
order by me.member_id desc
